import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { patientsAPI } from '../api/api';

const PatientProfile = () => {
  const { id } = useParams();
  const [patient, setPatient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [tab, setTab] = useState('scans');

  const [scanForm, setScanForm] = useState({
    name: '',
    scanType: '',
    date: '',
    notes: ''
  });

  const [scanFile, setScanFile] = useState(null);

  const loadPatient = async () => {
    const res = await patientsAPI.getById(id);
    setPatient(res.data);
    setLoading(false);
  };

  useEffect(() => {
    loadPatient();
  }, [id]);

  const submitScan = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', scanFile); // ðŸ”¥ MUST BE "file"
    formData.append('name', scanForm.name);
    formData.append('scanType', scanForm.scanType);
    formData.append('date', scanForm.date);
    formData.append('notes', scanForm.notes);

    await patientsAPI.addScan(id, formData);
    setScanForm({ name: '', scanType: '', date: '', notes: '' });
    setScanFile(null);
    loadPatient();
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="container">
      <h2>{patient.firstName} {patient.lastName}</h2>

      <form onSubmit={submitScan} encType="multipart/form-data">
        <input placeholder="Scan Name" onChange={e => setScanForm({ ...scanForm, name: e.target.value })} required />
        <input placeholder="Scan Type" onChange={e => setScanForm({ ...scanForm, scanType: e.target.value })} required />
        <input type="date" onChange={e => setScanForm({ ...scanForm, date: e.target.value })} required />
        <input type="file" accept="image/*" onChange={e => setScanFile(e.target.files[0])} required />
        <textarea placeholder="Notes" onChange={e => setScanForm({ ...scanForm, notes: e.target.value })} />
        <button>Add Scan</button>
      </form>

      <hr />

      {patient.scans.map(s => (
        <div key={s.id}>
          <p><b>{s.name}</b> ({s.scanType})</p>
          <img
            src={`http://13.127.142.221:3001${s.imageUrl}`}
            alt={s.name}
            style={{ maxWidth: '300px' }}
          />
        </div>
      ))}
    </div>
  );
};

export default PatientProfile;
